          <div className="container">
            {/* Responsive Table Section  */}
            <table className="responsive-table">
              {/* Responsive Table Header Section */}
              <thead className="responsive-table__head">
                <tr className="responsive-table__row">
                  <th className="responsive-table__head__title responsive-table__head__title--name">Token ID</th>
                  <th className="responsive-table__head__title responsive-table__head__title--status">Total Value</th>
                  <th className="responsive-table__head__title responsive-table__head__title--types">Timestamp</th>
