import React, {useEffect} from 'react';
import { useQuery, gql, ApolloProvider} from '@apollo/client';
import RefreshButton from './components/Button/RefreshButton';
import PaginationButton from './components/PaginationButton/PaginationButton';


const GET_DATA = gql`
  query GetTopPools {
    pools(first: 10, orderBy: totalValueLockedUSD, orderDirection: desc, after: null) {
      id
      totalValueLockedUSD
      volumeUSD
    },
    tokens(first: 10, skip:  orderBy: name, orderDirection: desc) {
      id
      symbol
      name
      totalValueLocked
      volumeUSD
    },
    uery Transactions(first: 10, orderBy: id) {
    swaps(first: $first, skip: $skip, orderBy: $orderBy, orderDirection: desc) {
      id
      timestamp
      amount0In
      amount0Out
      amount1In
      amount1Out
      amountUSD
      sender
      pair {
        token0 {
          symbol
        }
        token1 {
          symbol
        }
      }
    }
  }
  }
`;

function App() {
  const { loading, error, data, refetch } = useQuery(GET_DATA);

  useEffect(() => {
    console.log('data', data)
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <ApolloProvider>
      <div className='container'>
        <div className='container__block'>
          <div className="container__block__table-header">
          <h4 className="container__block__table-header__title">Test Header</h4>
          {/* refresh button */}
          <RefreshButton
            text="Refresh"
            click={() => refetch()}
          />
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Pool ID</th>
                <th>Total Value Locked (USD)</th>
                <th>24Hr Volume (USD)</th>
              </tr>
            </thead>
            <tbody>
              {data.pools.map((pool) => (
                <tr key={pool.id}>
                  <td>{pool.id}</td>
                  <td>{pool.totalValueLockedUSD}</td>
                  <td>{pool.volumeUSD}</td>
                </tr>
              ))}

            </tbody>
          </table>

          {/* tokens */}
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Token ID</th>
                <th>Symbol</th>
                <th>Name</th>
                <th>totalValueLocked</th>
                <th>Volume</th>
              </tr>
            </thead>
            <tbody>
              {data.tokens.map((token) => (
                <tr key={token.id}>
                  <td>{token.id}</td>
                  <td>{token.symbol}</td>
                  <td>{token.name}</td>
                  <td>{token.totalValueLocked}</td>
                  <td>{token.volumeUSD}</td>
                </tr>
              ))}

            </tbody>
          </table>


        </div>
      </div>

    </ApolloProvider>

  );
}

export default App;
