import React from 'react';
import { gql, useQuery } from '@apollo/client';

const GET_TOP_POOLS = gql`
  query TopPools($limit: Int!, $orderBy: String!, $orderDirection: String!) {
    pools(first: $limit, orderBy: $orderBy, orderDirection: $orderDirection) {
      id
      feeTier
      totalValueLockedUSD
      dailyVolumeUSD
    }
  }
`;

function App({ limit = 10, orderBy = 'totalValueLockedUSD', orderDirection = 'desc' }) {
  const { loading, error, data } = useQuery(GET_TOP_POOLS, {
    variables: { limit, orderBy, orderDirection },
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <table>
      <thead>
        <tr>
          <th>Pool ID</th>
          <th>Fee Tier</th>
          <th>Total Value Locked (USD)</th>
          <th>Daily Volume (USD)</th>
        </tr>
      </thead>
      <tbody>
        {data.pools.map((pool) => (
          <tr key={pool.id}>
            <td>{pool.id}</td>
            <td>{pool.feeTier}</td>
            <td>{pool.totalValueLockedUSD}</td>
            <td>{pool.dailyVolumeUSD}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default App;
