import React from 'react';
import { useQuery, gql, ApolloProvider } from '@apollo/client';


const GET_TOP_POOLS = gql`
  query GetTopPools {
    pools(first: 10, orderBy: totalValueLockedUSD, orderDirection: desc) {
      id
      totalValueLockedUSD
      volumeUSD
    }
  }
`;

function App() {
  const { topPoolsLoading, topPoolsError, topPools } = useQuery(GET_TOP_POOLS);

  if (topPoolsLoading) return <p>Loading...</p>;
  if (topPoolsError) return <p>Error :(</p>;

  return (
    <ApolloProvider>
      <div className='container'>
        {/* <img className="header-logo" src={uniswapLogo} alt="uniswap logo" /> */}
        <table className='container__table'>
          <thead>
            <tr>
              <th>Pool ID</th>
              <th>Total Value Locked (USD)</th>
              <th>24Hr Volume (USD)</th>
            </tr>
          </thead>
          <tbody>
            {topPools.pools.map((_topPool) => (
              <tr key={_topPool.id}>
                <td>{_topPool.id}</td>
                <td>{_topPool.totalValueLockedUSD}</td>
                <td>{_topPool.volumeUSD}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

    </ApolloProvider>

  );
}

export default App;
