import React, {useEffect} from 'react';
import { useQuery, gql, ApolloProvider} from '@apollo/client';
import RefreshButton from './components/Button/RefreshButton';


const V3_SUBGRAPH_DATA = gql`
  query GetTopPools {
    pools(first: 10, orderBy: totalValueLockedUSD, orderDirection: desc, after: null) {
      id
      totalValueLockedUSD
      volumeUSD
    },
    tokens(first: 10, orderBy: name, orderDirection: desc) {
      id
      symbol
      totalValueLocked
      volumeUSD
    },
    swaps(first: 10, skip: 0, orderBy: timestamp, orderDirection: desc) {
      id
      amountUSD
      timestamp
      sender
    }
  }
`;

function App() {
  const { loading, error, data, refetch } = useQuery(V3_SUBGRAPH_DATA);

  useEffect(() => {
    console.log('data', data)

    // data.swaps.map((swap) => (
    //   console.log(swap.timestamp)
    // ))
  }, [data]);


  function timeDifference(current, previous) {
    const msPerMinute = 60 * 1000;
    const msPerHour = msPerMinute * 60;
    const msPerDay = msPerHour * 24;
    const msPerMonth = msPerDay * 30;
    const msPerYear = msPerDay * 365;
    let elapsed = current - previous;

    if (elapsed < msPerMinute) {
         return Math.round(elapsed/1000) + ' seconds ago';   
    }

    else if (elapsed < msPerHour) {
         return Math.round(elapsed/msPerMinute) + ' minutes ago';   
    }

    else if (elapsed < msPerDay ) {
         return Math.round(elapsed/msPerHour ) + ' hours ago';   
    }

    else if (elapsed < msPerMonth) {
        return 'approximately ' + Math.round(elapsed/msPerDay) + ' days ago';   
    }

    else if (elapsed < msPerYear) {
        return 'approximately ' + Math.round(elapsed/msPerMonth) + ' months ago';   
    }

    else {
        return 'approximately ' + Math.round(elapsed/msPerYear ) + ' years ago';   
    }
}

  if (loading) {
    return  <p>Loading...</p>;
  } else if (error) {
    return  <p>Error :</p>;
  } else {
    console.log('data', data)

    data.swap.forEach(swap => {
       console.log(swap.timestamp)
    });
  }


  return (
    <ApolloProvider>
      <div className='container'>
        {/* refresh button */}
        <RefreshButton
          text="Refresh"
          click={() => refetch()}
        />
        <div className='container__block'>
          <div className="container__block__table-header">
            <h4 className="container__block__table-header__title">Top Pools</h4>
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Pool ID</th>
                <th>Total Value Locked (USD)</th>
                <th>24Hr Volume (USD)</th>
              </tr>
            </thead>
            <tbody>
              {data.pools.map((pool) => (
                <tr key={pool.id}>
                  <td>{pool.id}</td>
                  <td>{pool.totalValueLockedUSD}</td>
                  <td>{pool.volumeUSD}</td>
                </tr>
              ))}

            </tbody>
          </table>
        </div>



        <div className='container__block'>
          <div className="container__block__table-header">
            <h4 className="container__block__table-header__title">Tokens</h4>
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Token ID</th>
                <th>Symbol</th>
                <th>totalValueLocked</th>
                <th>Volume</th>
              </tr>
            </thead>
            <tbody>
              {data.tokens.map((token) => (
                <tr key={token.id}>
                  <td>
                    <a 
                    className='td-link' 
                    href={'https://etherscan.io/address/' + token.id} 
                    target="_blank" 
                    rel="noreferrer">
                      {token.id}
                    </a>
                  </td>
                  <td>{token.symbol}</td>
                  <td>{token.totalValueLocked}</td>
                  <td>{token.volumeUSD}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>


        <div className='container__block'>
          <div className="container__block__table-header">
            <h4 className="container__block__table-header__title">Transactions</h4>
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Token ID</th>
                <th>Total Value</th>
                <th>Timestamp</th>
                <th>Sender</th>
              </tr>
            </thead>
            <tbody>
              {data.swaps.map((swap) => (
                <tr key={swap.id}>
                  <td>
                    <a
                    className='td-link' 
                    href={'https://etherscan.io/tx/' + swap.id} 
                    target="_blank" 
                    rel="noreferrer">
                      {swap.id}
                    </a>
                  </td>
                  <td>{swap.amountUSD}</td>
                  <td>{swap.timestamp}</td>
                  <td>{swap.sender}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </div>

    </ApolloProvider>

  );
}

export default App;
