import React, {useEffect} from 'react';
import { useQuery, gql, ApolloProvider} from '@apollo/client';


const GET_TOP_POOLS = gql`
  query GetTopPools {
    pools(first: 10, orderBy: totalValueLockedUSD, orderDirection: desc) {
      id
      totalValueLockedUSD
      volumeUSD
    }
  }
`;

function App() {
  const { loading, error, data, refetch } = useQuery(GET_TOP_POOLS);

  useEffect(() => {
    console.log('data', data)
  }, [])


  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <ApolloProvider>
      <div className='container'>
        {/* <img className="header-logo" src={uniswapLogo} alt="uniswap logo" /> */}
        <div className='container__block'>
          <div className="container__block__table-header">
          <h4>Test Header</h4>
          <button onClick={() => refetch()}>Refresh</button>
          </div>
          <table className='container__table'>
            <thead>
              <tr>
                <th>Pool ID</th>
                <th>Total Value Locked (USD)</th>
                <th>24Hr Volume (USD)</th>
              </tr>
            </thead>
            <tbody>
              {data.pools.map((pool) => (
                <tr key={pool.id}>
                  <td>{pool.id}</td>
                  <td>{pool.totalValueLockedUSD}</td>
                  <td>{pool.volumeUSD}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

    </ApolloProvider>

  );
}

export default App;
