import React, {useEffect} from 'react';
import { useQuery, gql, ApolloProvider} from '@apollo/client';
import RefreshButton from './components/Button/RefreshButton';
import PaginationButton from './components/PaginationButton/PaginationButton';


const GET_DATA = gql`
  query GetTopPools {
    pools(first: 40, orderBy: totalValueLockedUSD, orderDirection: desc, after: null) {
      id
      totalValueLockedUSD
      volumeUSD
    }
  }
  query Tokens($first: Int!, $skip: Int!, $orderBy: String!) {
    tokens(first: $first, skip: $skip, orderBy: $orderBy, orderDirection: desc) {
      id
      symbol
      name
      derivedETH
      totalValueLocked
      volumeUSD
    }
  }
`;

function App() {
  const { loading, error, data, refetch } = useQuery(GET_DATA);

  useEffect(() => {
    console.log('data', data)
  }, []);

  const increment = () => {
    console.log('increment')
  }


  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <ApolloProvider>
      <div className='container'>
        <div className='container__block'>
          <div className="container__block__table-header">
          <h4 className="container__block__table-header__title">Test Header</h4>
          {/* refresh button */}
          <RefreshButton
            text="Refresh"
            click={() => refetch()}
          />
          {/* <button className="container__block__table-header__button" onClick={() => refetch()}>Refresh</button> */}
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Pool ID</th>
                <th>Total Value Locked (USD)</th>
                <th>24Hr Volume (USD)</th>
              </tr>
            </thead>
            <tbody>
              {data.pools.map((pool) => (
                <tr key={pool.id}>
                  <td>{pool.id}</td>
                  <td>{pool.totalValueLockedUSD}</td>
                  <td>{pool.volumeUSD}</td>
                </tr>
              ))}

            </tbody>
          </table>

          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Pool ID</th>
                <th>Total Value Locked (USD)</th>
                <th>24Hr Volume (USD)</th>
              </tr>
            </thead>
            <tbody>
              {data.tokens.map((token) => (
                <tr key={token.id}>
                  <td>{token.id}</td>
                  <td>{token.symbol}</td>
                  <td>{token.name}</td>
                </tr>
              ))}

            </tbody>
          </table>
           {/* pagination section */}
          <div className='pagination-row'>
            <PaginationButton
              text="Previous"
              click={increment}
            />
            <PaginationButton
              text="Next"
              click={increment}
            />
          </div>
        </div>
      </div>

    </ApolloProvider>

  );
}

export default App;
