import React, {useEffect, useState} from 'react';
import { useQuery, gql, ApolloProvider} from '@apollo/client';
import RefreshButton from './components/Button/RefreshButton';
// import './components/Gradient.js'

const V3_SUBGRAPH_DATA = gql`
  query GetTopPools {
    pools(first: 10, orderBy: totalValueLockedUSD, orderDirection: desc, after: null) {
      id
      totalValueLockedUSD
      volumeUSD
    },
    tokens(first: 10, orderBy: name, orderDirection: desc) {
      id
      symbol
      totalValueLocked
      volumeUSD
    },
    swaps(first: 10, skip: 0, orderBy: timestamp, orderDirection: desc) {
      id
      amountUSD
      timestamp
      sender
    }
  }
`;

function App() {
  const { loading, error, data, refetch } = useQuery(V3_SUBGRAPH_DATA);
  const [timeElapsed, setTimeElapsed] = useState('')

  useEffect(() => {
    // loops over swap times
    if (data) {
      data.swaps.forEach(swap => {
        // calls the timeDifference() fn and passes in current Datetime and the swap timestamp as the args
        timeDifference(Math.floor(Date.now() / 1000), swap.timestamp);
      });
    }

    const script = document.createElement("script");
    script.src = "./components/Gradient.js";
    script.async = true;
    document.body.appendChild(script);
  }, [data]);

  // calculates the time since swap
  function timeDifference(current, previous) {
    const msPerMinute = 60 * 1000;
    const msPerHour = msPerMinute * 60;
    const msPerDay = msPerHour * 24;
    const msPerMonth = msPerDay * 30;
    const msPerYear = msPerDay * 365;
    let elapsed = current - previous;

    if (elapsed < msPerMinute) {
      setTimeElapsed(Math.round(elapsed/1000) + ' seconds ago')
    } else if (elapsed < msPerHour) {
      setTimeElapsed(Math.round(elapsed/msPerMinute) + ' minutes ago')
    } else if (elapsed < msPerDay ) {
      setTimeElapsed(Math.round(elapsed/msPerHour ) + ' hours ago')
    } else if (elapsed < msPerMonth) {
      setTimeElapsed('approximately ' + Math.round(elapsed/msPerDay) + ' days ago')
    } else if (elapsed < msPerYear) {
      setTimeElapsed('approximately ' + Math.round(elapsed/msPerMonth) + ' months ago')
    } else {
      setTimeElapsed('approximately ' + Math.round(elapsed/msPerYear ) + ' years ago')
    }
  }


  // checks to see if data is loading or if there's an error
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :</p>;

  return (
    <ApolloProvider>
      <canvas id="gradient-canvas" data-js-darken-top data-transition-in />
      <div className='container'>
        {/* refresh button */}
        <RefreshButton
          text="Refresh"
          // calls the refetch() fn onclick, to refetch data
          click={() => refetch()}
        />
        <div className='container__block'>
          <div className="container__block__table-header">
            <h4 className="container__block__table-header__title">Top Pools</h4>
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Pool ID</th>
                <th>Total Value Locked (USD)</th>
                <th>24Hr Volume (USD)</th>
              </tr>
            </thead>
            <tbody>
              {data.pools.map((pool) => (
                <tr key={pool.id}>
                  <td>{pool.id}</td>
                  <td>{pool.totalValueLockedUSD}</td>
                  <td>{pool.volumeUSD}</td>
                </tr>
              ))}

            </tbody>
          </table>
        </div>



        <div className='container__block'>
          <div className="container__block__table-header">
            <h4 className="container__block__table-header__title">Tokens</h4>
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Token ID</th>
                <th>Symbol</th>
                <th>totalValueLocked</th>
                <th>Volume</th>
              </tr>
            </thead>
            <tbody>
              {data.tokens.map((token) => (
                <tr key={token.id}>
                  <td>
                    <a 
                    className='td-link' 
                    href={'https://etherscan.io/address/' + token.id} 
                    target="_blank" 
                    rel="noreferrer">
                      {token.id}
                    </a>
                  </td>
                  <td>{token.symbol}</td>
                  <td>{token.totalValueLocked}</td>
                  <td>{token.volumeUSD}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>


        <div className='container__block'>
          <div className="container__block__table-header">
            <h4 className="container__block__table-header__title">Transactions</h4>
          </div>
          <table className='table'>
            <thead className='table-head'>
              <tr>
                <th>Token ID</th>
                <th>Total Value</th>
                <th>Timestamp</th>
                <th>Sender</th>
              </tr>
            </thead>
            <tbody>
              {data.swaps.map((swap) => (
                <tr key={swap.id}>
                  <td>
                    <a
                    className='td-link' 
                    href={'https://etherscan.io/tx/' + swap.id} 
                    target="_blank" 
                    rel="noreferrer">
                      {swap.id}
                    </a>
                  </td>
                  <td>{swap.amountUSD}</td>
                  <td>{timeElapsed}</td>
                  <td alt="lol">{swap.sender}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </div>

    </ApolloProvider>

  );
}

export default App;
