import React, {useEffect} from 'react';
import { useQuery, gql, ApolloProvider} from '@apollo/client';
import RefreshButton from './components/Button/RefreshButton';
import PaginationButton from './components/PaginationButton/PaginationButton';


const GET_TOP_POOLS = gql`
  query GetTopPools {
    pools(first: 10, orderBy: totalValueLockedUSD, orderDirection: desc, after: null) {
      id
      totalValueLockedUSD
      volumeUSD
    }
  }
`;

function App() {
  const { loading, error, data, refetch } = useQuery(GET_TOP_POOLS);

  useEffect(() => {
    console.log('data', data)
  }, []);

  const increment = () => {
    console.log('increment')
  }


  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <ApolloProvider>
      <div className='container'>
        <div className='container__block'>
          <div className="container__block__table-header">
          <h4 className="container__block__table-header__title">Test Header</h4>
          {/* refresh button */}
          <RefreshButton
            text="Refresh"
            click={() => refetch()}
          />
          {/* <button className="container__block__table-header__button" onClick={() => refetch()}>Refresh</button> */}
          </div>
          <table className='container__block__table'>
            <thead>
              <tr>
                <th>Pool ID</th>
                <th>Total Value Locked (USD)</th>
                <th>24Hr Volume (USD)</th>
              </tr>
            </thead>
            <tbody>
              {data.pools.map((pool) => (
                <tr key={pool.id}>
                  <td>{pool.id}</td>
                  <td>{pool.totalValueLockedUSD}</td>
                  <td>{pool.volumeUSD}</td>
                </tr>
              ))}

            </tbody>
          </table>

          <div>
            <h2>Tokens Table</h2>
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Symbol</th>
                  <th>Price (ETH)</th>
                  <th>24h Change</th>
                  <th>TVL</th>
                </tr>
              </thead>
              <tbody>
                {tokens.map((token) => (
                  <tr key={token.id}>
                    <td>{token.name}</td>
                    <td>{token.symbol}</td>
                    <td>{token.derivedETH}</td>
                    <td>{token.volumeUSD}</td>
                    <td>{token.totalLiquidity}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <Pagination
              count={10} //total number of pages
              page={currentPage}
              onChange={handlePageChange}
            />
          </div>


        </div>
      </div>

    </ApolloProvider>

  );
}

export default App;
