import React from 'react';
import { useQuery, gql, ApolloProvider } from '@apollo/client';

const GET_TOP_POOLS = gql`
  query GetTopPools {
    pools(first: 10, orderBy: totalValueLockedUSD, orderDirection: desc) {
      id
      totalValueLockedUSD
      volumeUSD
    }
  }
`;

function App() {
  const { loading, error, data } = useQuery(GET_TOP_POOLS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <ApolloProvider>
       <table>
      <thead>
        <tr>
          <th>Pool ID</th>
          <th>Total Value Locked (USD)</th>
          <th>24Hr Volume (USD)</th>
        </tr>
      </thead>
      <tbody>
        {data.pools.map((pool) => (
          <tr key={pool.id}>
            <td>{pool.id}</td>
            <td>{pool.totalValueLockedUSD}</td>
            <td>{pool.volumeUSD}</td>
          </tr>
        ))}
      </tbody>
    </table>
    </ApolloProvider>

  );
}

export default App;
