import { gql, useQuery } from '@apollo/client';

const GET_TOP_POOLS = gql`
  query topPools {
    pools(first: 10, orderBy: volumeUSD, orderDirection: desc) {
      id
      token0 {
        symbol
      }
      token1 {
        symbol
      }
      volumeUSD
      volumeToken0
      volumeToken1
    }
  }
`;

function TopPools() {
  const { loading, error, data } = useQuery(GET_TOP_POOLS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return (
    <table>
      <thead>
        <tr>
          <th>Pool ID</th>
          <th>Token 1</th>
          <th>Token 2</th>
          <th>24Hr Volume (USD)</th>
          <th>Total Volume (Token 1)</th>
          <th>Total Volume (Token 2)</th>
        </tr>
      </thead>
      <tbody>
        {data.pools.map((pool) => (
          <tr key={pool.id}>
            <td>{pool.id}</td>
            <td>{pool.token0.symbol}</td>
            <td>{pool.token1.symbol}</td>
            <td>{pool.volumeUSD}</td>
            <td>{pool.volumeToken0}</td>
            <td>{pool.volumeToken1}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

function App() {
  return (
    <div>
      <h1>Uniswap V3 Data Visualization</h1>
      <TopPools />
      {/* Add more components for tokens and transactions */}
    </div>
  );
}
